      },
    })

    const ball = Bodies.circle(150, 0, 10, {
      restitution: 0.9,
      render: {
        fillStyle: 'yellow',
      },
    })

    World.add(engine.world, [floor, ball])

    Engine.run(engine)
    Render.run(render)

    setContraints(boxRef.current.getBoundingClientRect())
